import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { RxwebValidators } from '@rxweb/reactive-form-validators';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {

  displayError:string='';
  isLoading:boolean=false;
  registerForm: FormGroup = new FormGroup({
    name: new FormControl(null, [
      Validators.required,
      Validators.maxLength(15),
      Validators.minLength(3),
    ]),
    email: new FormControl(null, [Validators.required, Validators.email]),
    password: new FormControl(null, [
      Validators.required,
      Validators.pattern(/^\w{6,}$/),
    ]),
    rePassword: new FormControl(null, [
      Validators.required,
      RxwebValidators.compare({fieldName:'password' })
    ]),
    phone: new FormControl(null, [
      Validators.required,
      Validators.pattern(/^01[0125][0-9]{8}$/),
    ]),
  });


constructor( private _authService:AuthService , private _route:Router){



}

  register(form: FormGroup) {
    console.log(form);

    this.allControlsTouched(form);
      this.displayError=''
    if(form.valid && !this.isLoading){
    this.isLoading=true;
     this._authService.signUp(form.value).subscribe(
      {
         next:(data)=>{

          console.log(data);
          if(data.massege=='sucess'){
            this._route.navigate(['/login'])
          }
         },
         error:(err)=>{
          console.log(err);
          this.displayError=err.error.message;

          this.isLoading=false
         }

      }
     )
    }
  }

  allControlsTouched(form: FormGroup) {
    // array of value
    Object.values(form.controls).forEach((control: any) => {
      control.markAsTouched();
      if (control.controls) {
        this.allControlsTouched(control);
      }
    });
  }
}
<div class="container  py-5">

   <div class="w-75 mx-auto">
    <h1>Register Now :</h1>
    <form  [formGroup]="registerForm" (ngSubmit)="register(registerForm)" >

<div class="alert alert-danger" *ngIf="displayError">
    {{displayError}}

</div>
    <div class="form-group mb-3">
        <label for="fullName">Full Name</label>
        <input type="text" id="fullName" class="form-control" formControlName="name">
        <div class="alert alert-danger mb-3" *ngIf="registerForm.get('name')?.errors && registerForm.get('name')?.touched">
            <p *ngIf="registerForm.get('name')?.getError('required')">required</p>
            <p *ngIf="registerForm.get('name')?.getError('maxlength')">maxLength you must 15</p>
            <p *ngIf="registerForm.get('name')?.getError('minlength')">minlength you must 3</p>

           </div>
    </div>


    <div class="form-group mb-3">
        <label for="email">Email</label>
        <input type="email" id="email" class="form-control" formControlName="email">

        <div class="alert alert-danger mb-3" *ngIf="registerForm.get('email')?.errors  && registerForm.get('email')?.touched">
            <p *ngIf="registerForm.get('email')?.getError('required')">Email required</p>
            <p *ngIf="registerForm.get('email')?.getError('email')">Email InValid Format</p>
           </div>
    </div>


    <div class="form-group mb-3">
        <label for="pass">Password</label>
        <input type="password" id="pass" class="form-control" formControlName="password">

        <div class="alert alert-danger mb-3" *ngIf="registerForm.get('password')?.errors  && registerForm.get('password')?.touched">
            <p *ngIf="registerForm.get('password')?.getError('required')">password required</p>
            <p *ngIf="registerForm.get('password')?.getError('pattern')"> must be at least 6 chars</p>
           </div>

    </div>

    <div class="form-group mb-3">
        <label for="rePassword">RePassword</label>
        <input type="password" id="rePassword" class="form-control" formControlName="rePassword">
    </div>
    <div class="alert alert-danger mb-3" *ngIf="registerForm.get('rePassword')?.errors  && registerForm.get('rePassword')?.touched">
        <p *ngIf="registerForm.get('rePassword')?.getError('required')"> rePessword required</p>
        <p *ngIf="registerForm.get('rePassword')?.getError('compare')">Password confirmation is incorrect</p>

       </div>

    <div class="form-group mb-3">
        <label for="phone">Phone</label>
        <input type="tel" id="phone" class="form-control" formControlName="phone">
    </div>
    <div class="alert alert-danger mb-3" *ngIf="registerForm.get('phone')?.errors   && registerForm.get('phone')?.touched">
        <p *ngIf="registerForm.get('phone')?.getError('required')">Phone required</p>
        <p *ngIf="registerForm.get('phone')?.getError('invaild')">accept only egypt phone numbers</p>
       </div>



  <button class="btn bg-main text-white d-block ms-auto" [disabled]="registerForm.invalid" >

    <i *ngIf="isLoading" class="fa fa-spinner fa-spin "></i>
    <span *ngIf="!isLoading">Register</span>

</button>


    </form>



   </div>







    </div>
